import React, { Fragment, useState } from "react";
import { Chart as ChartJS } from "chart.js/auto";
import { Chart } from "react-chartjs-2";
import styles from "./ChartsPainter.module.css";

const ChartsPainter = (props) => {
  const state = {
    labels: props.labels,
    datasets: [
      {
        label: "",
        backgroundColor: "#ccc",
        borderColor: "rgba(0,0,0,1)",
        borderWidth: 1,
        data: props.data,
      },
    ],
  };
  const options = {
    plugins: {
      legend: false,
      toolbar: false,
    },
  };
  const [selectedChart, setSelectedChart] = useState("bar");
  const lineChecked = () => {
      selectedChart = "line"
      console.log(123)
  }
  
  return (
    <Fragment>
      <div className={styles.painter}>
        <Chart
          type={selectedChart}
          data={state}
          options={options}
          width={7}
          height={5}
        />
      </div>
      <div className={styles.radio}>
        <input type='radio' id='bar' name='chart' defaultChecked/>
        <label htmlFor='bar'>Bar</label>

        <input type='radio' id='line' name='chart' onChange={lineChecked}/>
        <label htmlFor='line'>Line</label>

        <input type='radio' id='pie' name='chart' />
        <label htmlFor='pie'>Pie</label>

        <input type='radio' id='doughnut' name='chart' />
        <label htmlFor='doughnut'>Doughnut</label>
      </div>
    </Fragment>
  );
};

export default ChartsPainter;
