import React from "react";
import { Bar } from "react-chartjs-2";

const ChartsPainter = (props) => {
  const state = {
    labels: props.labels,
    datasets: [
      {
        label: "",
        backgroundColor: "rgba(75,192,192,1)",
        borderColor: "rgba(0,0,0,1)",
        borderWidth: 1,
        data: props.data,
      },
    ],
  };
  return (
    <div>
      <Bar
        data={state}
        options={{
          title: {
            display: true,
            text: "Average Rainfall per month",
            fontSize: 20,
          },
          legend: {
            display: false,
            position: "right",
          },
        }}
      />
    </div>
  );
};

export default ChartsPainter;
