import React, { Fragment } from "react";
import { Chart as ChartJS } from "chart.js/auto";
import { Chart } from "react-chartjs-2";
import styles from "./ChartsPainter.module.css";

const ChartsPainter = (props) => {
  const state = {
    labels: props.labels,
    datasets: [
      {
        label: "",
        backgroundColor: "#ccc",
        borderColor: "rgba(0,0,0,1)",
        borderWidth: 1,
        data: props.data,
      },
    ],
  };

  let datasets;
  let selectedChart = "bar";
  const options = {
    plugins: {
      legend: false,
      toolbar: false,
    },
  };

  return (
    <Fragment>
      <div className={styles.painter}>
        <Chart
          type={selectedChart}
          data={state}
          options={options}
          width={7}
          height={5}
        />
      </div>
      <div style={{display: "flex", justifyContent: "center"}}>
        <input type='radio' id='bar'/>
        <label htmlFor='bar'>Bar</label>

        <input type='radio' id='line'/>
        <label htmlFor='line'>Line</label>

        <input type='radio' id='pie'/>
        <label htmlFor='pie'>Pie</label>

        <input type='radio' id='doughnut'/>
        <label htmlFor='doughnut'>Doughnut</label>
      </div>
    </Fragment>
  );
};

export default ChartsPainter;
