import React, {useState, useRef} from "react";
import Card from "./UI/Card";
import styles from "./Charts.module.css"

const Charts = () => {
    const [value, setValue] = useState([{}])
    const inputX = useRef()
    const inputY = useRef()
    const checkInputsHandler = () => {
        console.log(inputX.current.value)
        console.log(inputY.current.value)
    }
  return (
    <Card className={styles.input}>
      <label htmlFor='X_axis'>X axis labels:</label>
      <input id='X_axis' type='text' ref={inputX}/>
      <label htmlFor='Y_axis'>Y axis labels:</label>
      <input id='Y_axis' type='text' ref={inputY}/>
      <button onClick={checkInputsHandler}>Check</button>
    </Card>
  );
};

export default Charts;
