import React, { useState, useRef } from "react";
import "./InputForm.module.css";

const InputForm = () => {
  const inputX = useRef();
  const inputY = useRef();

  const formSubmit = () => {
    const X_axis = inputX.current.value;
    const Y_axis = inputY.current.value;
    if (X_axis.trim().length < 1 || Y_axis.trim().value < 1) {
      alert("Please, enter value to both fields.");
    }
  };

  return (
    <div>
      <form onKeyPress={(e) => e.key === "Enter" && formSubmit()}>
        <label htmlFor='X_axis'>X axis labels:</label>
        <input id='X_axis' type='text' ref={inputX} />
        <label htmlFor='Y_axis'>Y axis labels:</label>
        <input id='Y_axis' type='text' ref={inputY} />
      </form>
    </div>
  );
};

export default InputForm;
