import React, { useState, useRef } from "react";
import "./InputForm.module.css";

const InputForm = () => {
  const inputX = useRef();
  const inputY = useRef();
  const formSubmit = () => {
    console.log(inputX.current.value);
    console.log(inputY.current.value);
  };
  return (
    <div>
      <form onKeyPress={(e) => e.key === "Enter" && formSubmit()}>
        <label htmlFor='X_axis'>X axis labels:</label>
        <input id='X_axis' type='text' ref={inputX} />
        <label htmlFor='Y_axis'>Y axis labels:</label>
        <input id='Y_axis' type='text' ref={inputY} />
      </form>
    </div>
  );
};

export default InputForm;
